function toggleTheme(){document.getElementById("btn-theme-toggle").addEventListener("click",(()=>{document.body.classList.contains("dark-theme")?document.body.classList.remove("dark-theme"):document.body.classList.add("dark-theme")}))}function initDownloadButtons(){let e=document.querySelectorAll(".btn-dld");e&&e.forEach((e=>{let t=e.querySelector(".btn-dld__link"),l=e.querySelector(".btn-dld__toggle"),o=e.querySelector(".btn-dld__collapse");l.addEventListener("click",(e=>{e.preventDefault(),t.classList.toggle("is-active"),l.classList.toggle("is-active"),o.classList.toggle("is-active")}))}))}window.onload=()=>{initDownloadButtons(),toggleTheme()};